mapper.select:
select 

from claim_bill_detail
where claim_bill_detail_id = #{claimBillDetailId,jdbcType=VARCHAR}
  and payment_item_code = #{paymentItemCode,jdbcType=VARCHAR}

mapper.delete:
delete from claim_bill_detail
where claim_bill_detail_id = #{claimBillDetailId,jdbcType=VARCHAR}
  and payment_item_code = #{paymentItemCode,jdbcType=VARCHAR}
