mapper.select:
select 

from claim_info
where claim_info_id = #{claimInfoId,jdbcType=VARCHAR}
  and claim_no = #{claimNo,jdbcType=VARCHAR}
  and report_no = #{reportNo,jdbcType=VARCHAR}
  and registration_no = #{registrationNo,jdbcType=VARCHAR}

mapper.delete:
delete from claim_info
where claim_info_id = #{claimInfoId,jdbcType=VARCHAR}
  and claim_no = #{claimNo,jdbcType=VARCHAR}
  and report_no = #{reportNo,jdbcType=VARCHAR}
  and registration_no = #{registrationNo,jdbcType=VARCHAR}
