mapper.select:
select 

from policy_share_coverage
where policy_share_coverage_id = #{policyShareCoverageId,jdbcType=VARCHAR}
  and policy_share_coverage_no = #{policyShareCoverageNo,jdbcType=VARCHAR}

mapper.delete:
delete from policy_share_coverage
where policy_share_coverage_id = #{policyShareCoverageId,jdbcType=VARCHAR}
  and policy_share_coverage_no = #{policyShareCoverageNo,jdbcType=VARCHAR}
